import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { connection, startConnection } from "../services/signalRService";
import api from "../services/api";

interface Message {
  id: number;
  ticketId: number;
  author: string;
  content: string;
  sentAt: string;
}

const TicketDetail: React.FC = () => {
  const { ticketId } = useParams<{ ticketId: string }>();
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const role = localStorage.getItem("role") || "user";
  const nickname = role === "admin" ? "Admin" : "User";

  useEffect(() => {
    const loadMessages = async () => {
      const response = await api.get(`/messages/${ticketId}`);
      setMessages(response.data);
    };

    const setupSignalR = async () => {
      await startConnection();

      // Prevent duplicate event handlers
      connection.off("ReceiveMessage");

      connection.on("ReceiveMessage", (message: Message) => {
        setMessages((prev) => [...prev, message]);
      });

      connection.invoke("JoinTicketGroup", parseInt(ticketId!));
    };

    loadMessages();
    setupSignalR();

    return () => {
      connection.invoke("LeaveTicketGroup", parseInt(ticketId!));
      connection.off("ReceiveMessage");
    };
  }, [ticketId]);

  const handleSendMessage = async () => {
    const message = {
      ticketId: parseInt(ticketId!),
      author: nickname,
      content: newMessage,
    };
    await api.post("/messages", message);
    setNewMessage("");
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h1 className="text-2xl font-bold">Ticket #{ticketId}</h1>
        <div className="mt-4">
          <h2 className="text-lg font-semibold">Messages</h2>
          <div className="space-y-4 mt-2 bg-gray-100 p-4 rounded-lg max-h-96 overflow-y-auto">
            {messages.map((msg) => (
              <div
                key={msg.id}
                className={`p-2 rounded shadow ${
                  msg.author === nickname ? "bg-blue-100" : "bg-white"
                }`}
              >
                <p>
                  <strong>{msg.author}</strong>: {msg.content}
                </p>
                <small className="text-gray-500">{new Date(msg.sentAt).toLocaleString()}</small>
              </div>
            ))}
          </div>
        </div>
        <div className="mt-4">
          <textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            className="w-full border rounded-lg p-2"
            placeholder="Type a message..."
          ></textarea>
          <button
            onClick={handleSendMessage}
            className="bg-blue-500 text-white mt-2 px-4 py-2 rounded-lg hover:bg-blue-600"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default TicketDetail;
