import axios from "axios";

// Konfiguracja klienta API
const api = axios.create({
    baseURL: "http://localhost:5000/api", // URL backendu
});

export const fetchTickets = async () => {
    const response = await api.get("/tickets");
    return response.data;
};

export const createTicket = async (ticket: any) => {
    const response = await api.post("/tickets", ticket);
    return response.data;
};

export const updateTicket = async (id: number, ticket: any) => {
    const response = await api.put(`/tickets/${id}`, ticket);
    return response.data;
};

export default api;
