import * as signalR from "@microsoft/signalr";

const connection = new signalR.HubConnectionBuilder()
  .withUrl("http://localhost:5000/realtimehub")
  .withAutomaticReconnect()
  .build();

let isConnectionStarted = false;

const startConnection = async () => {
  if (!isConnectionStarted && connection.state === "Disconnected") {
    try {
      await connection.start();
      isConnectionStarted = true;
      console.log("SignalR connected");
    } catch (err) {
      console.error("SignalR connection failed:", err);
      setTimeout(startConnection, 5000);
    }
  } else {
    console.log("SignalR connection already active or in progress.");
  }
};

connection.onclose(() => {
  console.log("SignalR disconnected");
  isConnectionStarted = false;
});

export { connection, startConnection };
