import React, { useEffect, useState } from "react";
import connection from "../services/signalRService";
import { fetchTickets } from "../services/api";

interface Ticket {
  id: number;
  title: string;
  description: string;
  priority: string;
  status: string;
}

const TicketsList: React.FC = () => {
  const [tickets, setTickets] = useState<Ticket[]>([]);

  useEffect(() => {
    const loadTickets = async () => {
      const data = await fetchTickets();
      setTickets(data);
    };

    loadTickets();

    connection.on("NewTicket", (ticket: Ticket) => {
      setTickets((prev) => [...prev, ticket]);
    });

    connection.on("UpdateTicket", (updatedTicket: Ticket) => {
      setTickets((prev) =>
        prev.map((ticket) =>
          ticket.id === updatedTicket.id ? updatedTicket : ticket
        )
      );
    });

    return () => {
      connection.off("NewTicket");
      connection.off("UpdateTicket");
    };
  }, []);

  return (
    <div className="bg-white shadow rounded-lg p-4">
      <h1 className="text-2xl font-bold mb-4">Tickets</h1>
      <ul className="space-y-4">
        {tickets.map((ticket) => (
          <li
            key={ticket.id}
            className="p-4 border rounded-lg bg-gray-100 hover:shadow-md"
          >
            <h2 className="text-lg font-semibold">{ticket.title}</h2>
            <p className="text-gray-600">{ticket.description}</p>
            <p
              className={`mt-2 text-sm font-medium ${
                ticket.priority === "High"
                  ? "text-red-500"
                  : ticket.priority === "Medium"
                  ? "text-yellow-500"
                  : "text-green-500"
              }`}
            >
              Priority: {ticket.priority}
            </p>
            <p className="mt-1 text-sm text-gray-500">Status: {ticket.status}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TicketsList;
