import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { connection, startConnection } from "../services/signalRService";
import api from "../services/api";

interface Message {
  id: number;
  ticketId: number;
  author: string;
  content: string;
  sentAt: string;
}

const TicketDetail: React.FC = () => {
    const { ticketId } = useParams<{ ticketId: string }>();
    const [messages, setMessages] = useState<Message[]>([]);
    const [newMessage, setNewMessage] = useState("");
    const [ticketStatus, setTicketStatus] = useState("");
    const role = localStorage.getItem("role") || "user";
    const nickname = role === "admin" ? "Admin" : "User";
  
    useEffect(() => {
      const loadMessagesAndStatus = async () => {
        const messagesResponse = await api.get(`/messages/${ticketId}`);
        setMessages(messagesResponse.data);
  
        const ticketResponse = await api.get(`/tickets`);
        const ticket = ticketResponse.data.find((t: Ticket) => t.id === parseInt(ticketId!));
        setTicketStatus(ticket?.status || "");
      };
  
      const setupSignalR = async () => {
        await startConnection();
  
        connection.off("ReceiveMessage");
  
        connection.on("ReceiveMessage", (message: Message) => {
          setMessages((prev) => [...prev, message]);
        });
  
        connection.on("UpdateTicket", (updatedTicket: Ticket) => {
          if (updatedTicket.id === parseInt(ticketId!)) {
            setTicketStatus(updatedTicket.status);
          }
        });
  
        connection.invoke("JoinTicketGroup", parseInt(ticketId!));
      };
  
      loadMessagesAndStatus();
      setupSignalR();
  
      return () => {
        connection.invoke("LeaveTicketGroup", parseInt(ticketId!));
        connection.off("ReceiveMessage");
        connection.off("UpdateTicket");
      };
    }, [ticketId]);
  
    const handleSendMessage = async () => {
      if (ticketStatus === "Closed") {
        alert("Chat is disabled because the ticket is closed.");
        return;
      }
  
      const message = {
        ticketId: parseInt(ticketId!),
        author: nickname,
        content: newMessage,
      };
      await api.post("/messages", message);
      setNewMessage("");
    };
  
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="bg-white shadow rounded-lg p-6">
          <h1 className="text-2xl font-bold">Ticket #{ticketId}</h1>
          <p className={`text-lg ${getStatusClass(ticketStatus)}`}>Status: {ticketStatus}</p>
          {ticketStatus === "Closed" && (
            <p className="text-red-500 mt-2">This ticket is closed. Chat is disabled.</p>
          )}
          <div className="mt-4">
            <h2 className="text-lg font-semibold">Messages</h2>
            <div className="space-y-4 mt-2 bg-gray-100 p-4 rounded-lg max-h-96 overflow-y-auto">
              {messages.map((msg) => (
                <div
                  key={msg.id}
                  className={`p-2 rounded shadow ${
                    msg.author === nickname ? "bg-blue-100" : "bg-white"
                  }`}
                >
                  <p>
                    <strong>{msg.author}</strong>: {msg.content}
                  </p>
                  <small className="text-gray-500">{new Date(msg.sentAt).toLocaleString()}</small>
                </div>
              ))}
            </div>
          </div>
          <div className="mt-4">
            <textarea
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              disabled={ticketStatus === "Closed"}
              className={`w-full border rounded-lg p-2 ${
                ticketStatus === "Closed" ? "bg-gray-200 cursor-not-allowed" : ""
              }`}
              placeholder="Type a message..."
            ></textarea>
            <button
              onClick={handleSendMessage}
              disabled={ticketStatus === "Closed"}
              className={`mt-2 px-4 py-2 rounded-lg ${
                ticketStatus === "Closed"
                  ? "bg-gray-400 cursor-not-allowed text-white"
                  : "bg-blue-500 text-white hover:bg-blue-600"
              }`}
            >
              Send
            </button>
          </div>
        </div>
      </div>
    );
  };
  

export default TicketDetail;
