import React, { useEffect, useState } from "react";
import { connection, startConnection } from "../services/signalRService";
import api from "../services/api";
import { Link } from "react-router-dom";
import { getPriorityClass, getStatusClass } from "../utils";

interface Ticket {
  id: number;
  title: string;
  description: string;
  priority: string;
  status: string;
}

const AdminDashboard: React.FC = () => {
  const [tickets, setTickets] = useState<Ticket[]>([]);

  useEffect(() => {
    const fetchTickets = async () => {
      const response = await api.get("/tickets");
      setTickets(response.data);
    };

    const setupSignalR = async () => {
      await startConnection(); // Ensure connection starts only once

      // Prevent duplicate handlers
      connection.off("NewTicket");
      connection.off("UpdateTicket");

      connection.on("NewTicket", (ticket: Ticket) => {
        setTickets((prev) => [...prev, ticket]);
      });

      connection.on("UpdateTicket", (updatedTicket: Ticket) => {
        setTickets((prev) =>
          prev.map((ticket) =>
            ticket.id === updatedTicket.id ? updatedTicket : ticket
          )
        );
      });
    };

    fetchTickets();
    setupSignalR();

    // Cleanup handlers
    return () => {
      connection.off("NewTicket");
      connection.off("UpdateTicket");
    };
  }, []);

  const updateTicketStatus = async (ticketId: number, newStatus: string) => {
    try {
      console.log(`Updating ticket ${ticketId} to status: ${newStatus}`);
      const response = await api.put(`/tickets/${ticketId}`, { status: newStatus });
      console.log("Response:", response.data);
      setTickets((prev) =>
        prev.map((ticket) =>
          ticket.id === ticketId ? { ...ticket, status: newStatus } : ticket
        )
      );
    } catch (err) {
      console.error("Error updating ticket status:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>
      <div className="space-y-4">
        {tickets.map((ticket) => (
          <div key={ticket.id} className="p-4 bg-white shadow rounded-lg">
            <h2 className="text-lg font-semibold">{ticket.title}</h2>
            <p className="text-gray-600">{ticket.description}</p>
            <p className="text-gray-500">Priority: {ticket.priority}</p>
            <p className="text-gray-500">Status: {ticket.status}</p>
            <div className="mt-4 flex space-x-2">
              <button
                onClick={() => updateTicketStatus(ticket.id, "In Progress")}
                className="bg-yellow-500 text-white px-4 py-2 rounded-lg hover:bg-yellow-600"
              >
                Mark In Progress
              </button>
              <button
                onClick={() => updateTicketStatus(ticket.id, "Closed")}
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
              >
                Close Ticket
              </button>
              <Link
                to={`/tickets/${ticket.id}`}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
              >
                View Ticket
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
