import React from "react";
import { useNavigate } from "react-router-dom";

const RoleSelection: React.FC = () => {
  const navigate = useNavigate();

  const selectRole = (role: "admin" | "user") => {
    localStorage.setItem("role", role); // Save role for session persistence
    navigate(role === "admin" ? "/admin" : "/user");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="bg-white shadow rounded-lg p-6 space-y-4">
        <h1 className="text-2xl font-bold text-center">Select Your Role</h1>
        <button
          onClick={() => selectRole("user")}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg w-full hover:bg-blue-600"
        >
          I am a User
        </button>
        <button
          onClick={() => selectRole("admin")}
          className="bg-green-500 text-white px-4 py-2 rounded-lg w-full hover:bg-green-600"
        >
          I am an Admin
        </button>
      </div>
    </div>
  );
};

export default RoleSelection;
