import React, { useEffect, useState } from "react";
import connection from "../services/signalRService";
import api from "../services/api";
import TicketForm from "../components/TicketForm";
import { Link } from "react-router-dom";

interface Ticket {
  id: number;
  title: string;
  description: string;
  priority: string;
  status: string;
}

const UserDashboard: React.FC = () => {
  const [tickets, setTickets] = useState<Ticket[]>([]);

  useEffect(() => {
    const fetchTickets = async () => {
      const response = await api.get("/tickets");
      setTickets(response.data);
    };

    const setupSignalRConnection = async () => {
      if (connection.state === "Disconnected") {
        try {
          await connection.start();
          console.log("SignalR connected as User");

          connection.on("NewTicket", (ticket: Ticket) => {
            setTickets((prev) => [...prev, ticket]);
          });

          connection.on("UpdateTicket", (updatedTicket: Ticket) => {
            setTickets((prev) =>
              prev.map((ticket) =>
                ticket.id === updatedTicket.id ? updatedTicket : ticket
              )
            );
          });
        } catch (err) {
          console.error("Error starting SignalR connection:", err);
        }
      }
    };

    fetchTickets();
    setupSignalRConnection();

    return () => {
      connection.off("NewTicket");
      connection.off("UpdateTicket");
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold mb-6">User Dashboard</h1>
      <TicketForm />
      <div className="mt-6 space-y-4">
        {tickets.map((ticket) => (
          <div key={ticket.id} className="p-4 bg-white shadow rounded-lg">
            <h2 className="text-lg font-semibold">{ticket.title}</h2>
            <p className="text-gray-600">{ticket.description}</p>
            <p className="text-gray-500">Priority: {ticket.priority}</p>
            <p className="text-gray-500">Status: {ticket.status}</p>
            <div className="mt-4">
              <Link
                to={`/tickets/${ticket.id}`}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
              >
                View Ticket
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserDashboard;
